import{_ as e,c as t,o,a as n}from"./app.7be1093f.js";const b=JSON.parse(`{"title":"Admin Commands","description":"","frontmatter":{},"headers":[{"level":2,"title":"Set Prefix","slug":"set-prefix","link":"#set-prefix","children":[]},{"level":2,"title":"Automod Configuration","slug":"automod-configuration","link":"#automod-configuration","children":[]},{"level":2,"title":"Starboard","slug":"starboard","link":"#starboard","children":[]},{"level":2,"title":"Embed Builder","slug":"embed-builder","link":"#embed-builder","children":[{"level":3,"title":"What about fields?","slug":"what-about-fields","link":"#what-about-fields","children":[]}]},{"level":2,"title":"Verify System","slug":"verify-system","link":"#verify-system","children":[]},{"level":2,"title":"Reaction Roles Buttons","slug":"reaction-roles-buttons","link":"#reaction-roles-buttons","children":[{"level":3,"title":"Creating a new panel","slug":"creating-a-new-panel","link":"#creating-a-new-panel","children":[]},{"level":3,"title":"Adding a new role to the button panel","slug":"adding-a-new-role-to-the-button-panel","link":"#adding-a-new-role-to-the-button-panel","children":[]},{"level":3,"title":"Removing a role from a button panel","slug":"removing-a-role-from-a-button-panel","link":"#removing-a-role-from-a-button-panel","children":[]},{"level":3,"title":"Deleting a button panel","slug":"deleting-a-button-panel","link":"#deleting-a-button-panel","children":[]},{"level":3,"title":"How do I send a button panel?","slug":"how-do-i-send-a-button-panel","link":"#how-do-i-send-a-button-panel","children":[]},{"level":3,"title":"Where I find the ID's of my button panels?","slug":"where-i-find-the-id-s-of-my-button-panels","link":"#where-i-find-the-id-s-of-my-button-panels","children":[]}]},{"level":2,"title":"Autodelete","slug":"autodelete","link":"#autodelete","children":[]},{"level":2,"title":"Anti Commands","slug":"anti-commands","link":"#anti-commands","children":[]},{"level":2,"title":"Counter Channels","slug":"counter-channels","link":"#counter-channels","children":[]},{"level":2,"title":"Max Warn","slug":"max-warn","link":"#max-warn","children":[]},{"level":2,"title":"Flag Translations","slug":"flag-translations","link":"#flag-translations","children":[]},{"level":2,"title":"Moderation Logging","slug":"moderation-logging","link":"#moderation-logging","children":[]},{"level":2,"title":"Stats System","slug":"stats-system","link":"#stats-system","children":[]},{"level":2,"title":"XP system","slug":"xp-system","link":"#xp-system","children":[]},{"level":2,"title":"Greeting","slug":"greeting","link":"#greeting","children":[{"level":3,"title":"Welcome","slug":"welcome","link":"#welcome","children":[]},{"level":3,"title":"Farewell","slug":"farewell","link":"#farewell","children":[]}]},{"level":2,"title":"Reaction Roles","slug":"reaction-roles","link":"#reaction-roles","children":[{"level":3,"title":"Create Reaction Role","slug":"create-reaction-role","link":"#create-reaction-role","children":[]},{"level":3,"title":"Remove Reaction Roles","slug":"remove-reaction-roles","link":"#remove-reaction-roles","children":[]}]}],"relativePath":"commands/admin.md","lastUpdated":1671039555000}`),r={name:"commands/admin.md"},a=n('<h1 id="admin-commands" tabindex="-1">Admin Commands <a class="header-anchor" href="#admin-commands" aria-hidden="true">#</a></h1><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>These commands can be only be used by members who have <strong>ManageServer</strong> permission</p></div><h2 id="set-prefix" tabindex="-1">Set Prefix <a class="header-anchor" href="#set-prefix" aria-hidden="true">#</a></h2><ul><li><strong>Description</strong>: Set bot&#39;s prefix</li><li><strong>Usage</strong>: <code>!setprefix &lt;newPrefix&gt;</code></li></ul><h2 id="automod-configuration" tabindex="-1">Automod Configuration <a class="header-anchor" href="#automod-configuration" aria-hidden="true">#</a></h2><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><strong>!automod status</strong></td><td>view configuration status</td></tr><tr><td><strong>!automod strikes <code>&lt;amount&gt;</code></strong></td><td>Set the maximum number of strikes before taking an action</td></tr><tr><td><strong>!automod action <code>&lt;kick|mute|ban&gt;</code></strong></td><td>Set the action to be performed after receiving maximum strikes</td></tr><tr><td><strong>!automod debug <code>&lt;on|off&gt;</code></strong></td><td>turns on automod for messages sent by admins and moderators</td></tr><tr><td><strong>!automod whitelist</strong></td><td>view all whitelisted channels (all are whitelisted by default)</td></tr><tr><td><strong>!automod whitelistadd <code>&lt;channel&gt;</code></strong></td><td>add a channel to the whitelist</td></tr><tr><td><strong>!automod whitelistremove <code>&lt;channel&gt;</code></strong></td><td>remove a channel from the whitelist</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>By default, Auto moderation events are ignored for moderatos since they are assumed to be channel/server moderators</p><h4 id="ignored-permissions" tabindex="-1">Ignored Permissions: <a class="header-anchor" href="#ignored-permissions" aria-hidden="true">#</a></h4><p><strong>KickMembers</strong>, <strong>BanMembers</strong>, <strong>ManageGuild</strong>, <strong>ManageMessages</strong></p><p>Run <code>!automodoconfig debug on</code> to disable this.</p></div><h2 id="starboard" tabindex="-1">Starboard <a class="header-anchor" href="#starboard" aria-hidden="true">#</a></h2><table><thead><tr><th>Slash Command</th><th>Description</th></tr></thead><tbody><tr><td><strong>/starboard set <code>&lt;channel&gt; &lt;starnumber&gt;</code></strong></td><td>set the starboard channel and starCount for starboard</td></tr><tr><td><strong>/starboard reset</strong></td><td>Reset the starboard data</td></tr></tbody></table><h2 id="embed-builder" tabindex="-1">Embed Builder <a class="header-anchor" href="#embed-builder" aria-hidden="true">#</a></h2><p><strong><code>/embed &lt;channel&gt;</code></strong> Will sent a message with a button in the selected channel, on click it will show a modal (<strong>Form</strong>).</p><p>It wil require an <strong>Embed Title</strong>, <strong>Embed Author</strong>, <strong>Embed Description</strong>, <strong>Embed Color (HEX)</strong> and <strong>Embed Footer</strong>.</p><h3 id="what-about-fields" tabindex="-1">What about fields? <a class="header-anchor" href="#what-about-fields" aria-hidden="true">#</a></h3><p>Don&#39;t worry, after you fill the modal the bot will show buttons to add and remove fields, once you&#39;re done click on the <strong>Done</strong> button.</p><h2 id="verify-system" tabindex="-1">Verify System <a class="header-anchor" href="#verify-system" aria-hidden="true">#</a></h2><p><strong><code>/verify &lt;channel&gt; &lt;role&gt;</code></strong> Wil sent an embed with a button in the selected channel, on click it will give the member the selected role.</p><h2 id="reaction-roles-buttons" tabindex="-1">Reaction Roles Buttons <a class="header-anchor" href="#reaction-roles-buttons" aria-hidden="true">#</a></h2><p>Here&#39;s the configuration of the <strong>Reaction Roles Buttons</strong>.</p><h3 id="creating-a-new-panel" tabindex="-1">Creating a new panel <a class="header-anchor" href="#creating-a-new-panel" aria-hidden="true">#</a></h3><p><strong><code>/roles-menu create &lt;name&gt; &lt;description&gt; &lt;access-role&gt;</code></strong></p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Name</strong></td><td>The title to be displayed on the button panel.</td></tr><tr><td><strong>Description</strong></td><td>The description to be displayed on the button panel.</td></tr><tr><td><strong>Access-Role</strong></td><td>The role that can use the button panel (leave it blank for <strong>@everyone</strong>)</td></tr></tbody></table><h3 id="adding-a-new-role-to-the-button-panel" tabindex="-1">Adding a new role to the button panel <a class="header-anchor" href="#adding-a-new-role-to-the-button-panel" aria-hidden="true">#</a></h3><p><strong><code>/roles-menu add &lt;panel-id&gt; &lt;role&gt; &lt;emoji&gt;</code></strong></p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Panel-ID</strong></td><td>The ID of the panel that you wish to add a new role.</td></tr><tr><td><strong>Role</strong></td><td>The role to add to the button panel.</td></tr><tr><td><strong>Emoji</strong></td><td>The emoji to the button role (it will <strong>randomize</strong> if <strong>blank</strong>).</td></tr></tbody></table><h4 id="don-t-have-the-panel-id" tabindex="-1">Don&#39;t have the Panel-ID? <a class="header-anchor" href="#don-t-have-the-panel-id" aria-hidden="true">#</a></h4><p>Don&#39;t worry! Run <strong><code>/roles-menu list</code></strong> and look up for the panel that you wish to edit.</p><h3 id="removing-a-role-from-a-button-panel" tabindex="-1">Removing a role from a button panel <a class="header-anchor" href="#removing-a-role-from-a-button-panel" aria-hidden="true">#</a></h3><p><strong><code>/roles-menu remove &lt;panel-id&gt; &lt;role&gt;</code></strong></p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Panel-ID</strong></td><td>The ID of the panel that you wish to remove a role.</td></tr><tr><td><strong>Role</strong></td><td>The role to remove from the button panel.</td></tr></tbody></table><h4 id="don-t-have-the-panel-id-1" tabindex="-1">Don&#39;t have the Panel-ID? <a class="header-anchor" href="#don-t-have-the-panel-id-1" aria-hidden="true">#</a></h4><p>Don&#39;t worry! Run <strong><code>/roles-menu list</code></strong> and look up for the panel that you wish to edit.</p><h3 id="deleting-a-button-panel" tabindex="-1">Deleting a button panel <a class="header-anchor" href="#deleting-a-button-panel" aria-hidden="true">#</a></h3><p><strong><code>/roles-menu delete &lt;panel-id&gt;</code></strong></p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Panel-ID</strong></td><td>The ID of the panel that you wish to delete.</td></tr></tbody></table><h4 id="don-t-have-the-panel-id-2" tabindex="-1">Don&#39;t have the Panel-ID? <a class="header-anchor" href="#don-t-have-the-panel-id-2" aria-hidden="true">#</a></h4><p>Don&#39;t worry! Run <strong><code>/roles-menu list</code></strong> and look up for the panel that you wish to delete.</p><h3 id="how-do-i-send-a-button-panel" tabindex="-1">How do I send a button panel? <a class="header-anchor" href="#how-do-i-send-a-button-panel" aria-hidden="true">#</a></h3><p><strong><code>/roles-menu panel</code></strong></p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Panel-ID</strong></td><td>The ID of the panel that you wish to send.</td></tr><tr><td><strong>Channel</strong></td><td>Channel where the button panel will be send (it will send in the <strong>current</strong> channel if <strong>blank</strong>)</td></tr></tbody></table><h3 id="where-i-find-the-id-s-of-my-button-panels" tabindex="-1">Where I find the ID&#39;s of my button panels? <a class="header-anchor" href="#where-i-find-the-id-s-of-my-button-panels" aria-hidden="true">#</a></h3><p>Run <strong><code>/roles-menu list</code></strong></p><h2 id="autodelete" tabindex="-1">Autodelete <a class="header-anchor" href="#autodelete" aria-hidden="true">#</a></h2><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><strong>!autodelete attachments <code>on/off</code></strong></td><td>Allow or disallow attachments in messages</td></tr><tr><td><strong>!autodelete invites <code>on/off</code></strong></td><td>Allow or disallow sending discord invites in messages</td></tr><tr><td><strong>!autodelete links <code>on/off</code></strong></td><td>Allow or disallow sending links in messages</td></tr><tr><td><strong>!autodelete maxlines <code>number</code></strong></td><td>Sets maximum lines allowed per message [0 to disable]</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Each time a member tries to break the automated rule, he/she <strong>receives a strike</strong>. After receiving the maximum number of strikes (default 5), the moderation action (default <strong>TIMEOUT</strong>) is performed on them</p></div><h2 id="anti-commands" tabindex="-1">Anti Commands <a class="header-anchor" href="#anti-commands" aria-hidden="true">#</a></h2><p>Here are all the commands to avoid links,spam &amp; mass mentions</p><table><thead><tr><th>Name</th><th>Description</th></tr></thead><tbody><tr><td><strong>!anti ghostping <code>&lt;on/off&gt;</code></strong></td><td>logs ghost mentions in the server (Requires <code>/modlog</code> channel to be setup)</td></tr><tr><td><strong>!anti spam <code>&lt;on/off&gt;</code></strong></td><td>Stops users from spamming messages.</td></tr><tr><td><strong>!anti massmention <code>&lt;on/off&gt;</code> [threshold]</strong></td><td>Stops users from mass mentioning</td></tr></tbody></table><h2 id="counter-channels" tabindex="-1">Counter Channels <a class="header-anchor" href="#counter-channels" aria-hidden="true">#</a></h2><ul><li><strong>Usage</strong>: <code>!counter &lt;counter_type&gt; &lt;name&gt;</code></li><li><strong>Available counters</strong> <strong>types</strong><ul><li><strong><code>USERS</code></strong>: counts the total server member count (members + bots)</li><li><strong><code>MEMBERS</code></strong>: counts the total member count</li><li><strong><code>BOTS</code></strong>: counts the total number of bots</li></ul></li></ul><h2 id="max-warn" tabindex="-1">Max Warn <a class="header-anchor" href="#max-warn" aria-hidden="true">#</a></h2><ul><li><strong>!maxwarn limit <code>&lt;amount&gt;</code></strong>: set max warnings a member can receive before taking an action</li><li><strong>!maxwarn action <code>&lt;timeout|kick|ban&gt;</code></strong>: set the action to be performed after receiving maximum warnings</li></ul><h2 id="flag-translations" tabindex="-1">Flag Translations <a class="header-anchor" href="#flag-translations" aria-hidden="true">#</a></h2><p>Enabling this feature will allow members to simply react to any message with a country flag emoji, translating that message content to regional language.</p><ul><li><strong>Description</strong>: configure flag translation in the server</li><li><strong>Usage</strong>: <code>!flagtr &lt;on|off&gt;</code></li></ul><h2 id="moderation-logging" tabindex="-1">Moderation Logging <a class="header-anchor" href="#moderation-logging" aria-hidden="true">#</a></h2><ul><li><strong>Description</strong>: enable or disable moderation logs</li><li><strong>Usage</strong>: <code>!modlog &lt;channel|off&gt;</code></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Moderation logging enable logging of all <strong>moderation actions</strong> and <strong>automod events</strong></p></div><h2 id="stats-system" tabindex="-1">Stats System <a class="header-anchor" href="#stats-system" aria-hidden="true">#</a></h2><ul><li><strong>Description</strong>: configure the stats system in the server</li><li><strong>Usage</strong>: <code>!statstracking &lt;on|off&gt;</code></li></ul><h2 id="xp-system" tabindex="-1">XP system <a class="header-anchor" href="#xp-system" aria-hidden="true">#</a></h2><table><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><strong>!xp message <code>&lt;new-message&gt;</code></strong></td><td>set custom level up message</td></tr><tr><td><strong>!xp channel <code>&lt;#channel|off&gt;</code></strong></td><td>set the channel to send level up messages to</td></tr></tbody></table><h2 id="greeting" tabindex="-1">Greeting <a class="header-anchor" href="#greeting" aria-hidden="true">#</a></h2><p>Here are all <code>welcome/farewell</code> commands and <strong>Allowed Content Replacements</strong></p><h3 id="welcome" tabindex="-1">Welcome <a class="header-anchor" href="#welcome" aria-hidden="true">#</a></h3><table><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><strong>!welcome status <code>&lt;on/off&gt;</code></strong></td><td>enable or disable welcome message</td></tr><tr><td><strong>!welcome channel <code>&lt;#channel&gt;</code></strong></td><td>configure channel where welcome messages must be sent</td></tr><tr><td><strong>!welcome preview</strong></td><td>send a welcome preview</td></tr><tr><td><strong>!welcome desc <code>&lt;content&gt;</code></strong></td><td>set welcome embed description</td></tr><tr><td><strong>!welcome footer <code>&lt;content&gt;</code></strong></td><td>set welcome embed footer</td></tr><tr><td><strong>!welcome thumbnail <code>&lt;on/off&gt;</code></strong></td><td>enable or disable welcome message thumbnail</td></tr><tr><td><strong>!welcome color <code>&lt;#hex&gt;</code></strong></td><td>set welcome embed color</td></tr><tr><td><strong>!welcome image <code>&lt;url&gt;</code></strong></td><td>set welcome image at the bottom of the embed</td></tr></tbody></table><h3 id="farewell" tabindex="-1">Farewell <a class="header-anchor" href="#farewell" aria-hidden="true">#</a></h3><table><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><strong>!farewell status <code>&lt;on/off&gt;</code></strong></td><td>enable or disable farewell message</td></tr><tr><td><strong>!farewell channel <code>&lt;#channel&gt;</code></strong></td><td>configure channel where farewell messages must be sent</td></tr><tr><td><strong>!farewell preview</strong></td><td>send a welcome preview</td></tr><tr><td><strong>!farewell desc <code>&lt;content&gt;</code></strong></td><td>set farewell embed description</td></tr><tr><td><strong>!farewell footer <code>&lt;content&gt;</code></strong></td><td>set farewell embed footer</td></tr><tr><td><strong>!farewell thumbnail <code>&lt;on/off&gt;</code></strong></td><td>enable or disable farewell message thumbnail</td></tr><tr><td><strong>!farewell color <code>&lt;#hex&gt;</code></strong></td><td>set farewell embed color</td></tr><tr><td><strong>!farewell image <code>&lt;url&gt;</code></strong></td><td>set farewell image at the bottom of the embed</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Allowed Content Replacements</p><ul><li>\\n : New Line </li><li>{server} : Server Name </li><li>{count} : Server member count </li><li>{member:name} : Member Name </li><li>{member:tag} : Member Tag </li><li>{inviter:name} : Inviter Name </li><li>{inviter:tag} : Inviter Tag </li><li>{invites} : Inviter Invites</li></ul></div><h2 id="reaction-roles" tabindex="-1">Reaction Roles <a class="header-anchor" href="#reaction-roles" aria-hidden="true">#</a></h2><h3 id="create-reaction-role" tabindex="-1">Create Reaction Role <a class="header-anchor" href="#create-reaction-role" aria-hidden="true">#</a></h3><ul><li><strong>Usage</strong>: !addrr &lt;#channel&gt; &lt;messageId&gt; &lt;role&gt; &lt;emote&gt;</li><li><strong>Description</strong>: setup reaction role for the specified message</li></ul><h3 id="remove-reaction-roles" tabindex="-1">Remove Reaction Roles <a class="header-anchor" href="#remove-reaction-roles" aria-hidden="true">#</a></h3><ul><li><strong>Usage</strong>: !removerr &lt;#channel&gt; &lt;messageId&gt;</li><li><strong>Description</strong>: remove configured reaction for the specified message</li></ul>',73),d=[a];function l(s,i,h,c,g,m){return o(),t("div",null,d)}const p=e(r,[["render",l]]);export{b as __pageData,p as default};
